catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
	g_form.setValue('car_image','');
    return;
   }
   //Type appropriate comment here, and begin script below
	var image = g_form.getReference('car', doAlert); // doAlert is our callback function 
}
function doAlert(image) { //reference is passed into callback as first arguments

  g_form.setValue('car_image',image.car_image);

}
