catalog_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   var startoverlap = new GlideAjax("CarRentalUtils");
	startoverlap.addParam("sysparm_name", "isstartDateOverlappingend");
	startoverlap.addParam("sysparm_dateTime",  g_form.getValue('start_datetime'));
	startoverlap.addParam("sysparm_endTime", g_form.getValue('end_datetime'));
	startoverlap.addParam("sysparm_car", g_form.getValue('car'));

	startoverlap.getXML(isstartDateOverlappingend);



	function isstartDateOverlappingend(response){
			var answer = response.responseXML.documentElement.getAttribute("answer");
			if(answer == "true"){
				g_form.addErrorMessage("This car is already reserved on the selected date. Please pick another date.");
			}
	}

}
