catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	g_form.clearMessages();
	g_form.hideErrorBox('start_datetime');
	g_form.hideErrorBox('end_datetime');

	var today = new Date();
	var startDateTime = new Date(g_form.getValue('start_datetime'));
	var endDateTime = new Date(newValue);

	//If startdate > enddate
	if ((startDateTime > endDateTime) && (newValue != oldValue)) {
		g_form.showErrorBox('end_datetime', 'End Date/Time should not be earlier than Start Date/Time');
		return;
	}


//is//isDateWithinBusinessHours

	if(g_form.getValue("end_datetime") != "" ){

		var today = new Date();

		var end = new Date(g_form.getValue('end_datetime'));


		var enddatetime = end.getHours();






	if (enddatetime < 9 ||  enddatetime > 21) {
		g_form.showErrorBox('end_datetime', 'Start datetime must be within business hours: 9:00 - 21:00');
	}
	}



}
