catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	g_form.clearMessages();
	g_form.hideErrorBox('start_datetime');
	g_form.hideErrorBox('end_datetime');

	var today = new Date();
	var startDateTime = new Date(g_form.getValue('start_datetime'));
	var endDateTime = new Date(g_form.getValue('end_datetime'));

	//If startdate = same day
	var startDateFormatted = startDateTime.getDate() + "/" + (startDateTime.getMonth() + 1) + "/" + startDateTime.getFullYear();
	var todayFormatted = today.getDate() + "/" + (today.getMonth() + 1) + "/" + today.getFullYear();

	if ((todayFormatted == startDateFormatted) && (newValue != oldValue)) {
		g_form.showErrorBox('start_datetime', 'Reservation should not be on the same day');
		return;
	}

	//If stardate < today 
	if ((startDateTime < today) && (newValue != oldValue)) {
		g_form.showErrorBox('start_datetime', 'Date must not be in the past');
		return;
	}

	//If startdate is ahead of enddate 
	if ((startDateTime > endDateTime) && (newValue != oldValue)) {
		g_form.showErrorBox('start_datetime', 'Start date should not be ahead of End date');
		return;
	}

	//Business Days 
	if(g_form.getValue("end_datetime") != "" && g_form.getValue("car") != ""){
		var script = new GlideAjax("CarRentalUtils");
		script.addParam("sysparm_name", "isDateNotWithinBusinessDays");
		script.addParam("sysparm_sTime",  g_form.getValue("start_datetime"));
		script.addParam("sysparm_eTime", g_form.getValue("end_datetime"));
		script.addParam("sysparm_car", g_form.getValue("car"));


		script.getXML(isDateNotWithinBusinessDays);
	}

	function isDateNotWithinBusinessDays(response){
			var scriptResponse = response.responseXML.documentElement.getAttribute("answer");
			if(scriptResponse.includes("false")){
			var days = scriptResponse.split(" ");
				g_form.showErrorBox('start_datetime', 'Reservation must be within business days: Mon-Fri');

			}
	}

	/*//isDateWithinBusinessHours
	if(g_form.getValue("start_datetime") != "" ){

		var today = new Date();
		var start = new Date(g_form.getValue('start_datetime'));
		var startdatetime = start.getHours();

	if (startdatetime < 9 ||  startdatetime > 21) {
		g_form.showErrorBox('start_datetime', 'Reservation must be within business hours: 9:00 - 21:00');
	}

	}*/

	//isDateWithinBusinessHours
	if(g_form.getValue("start_datetime") != "" ){
	var scriptbhours = new GlideAjax("CarRentalUtils");
		scriptbhours.addParam("sysparm_name", "isstartDateWithinBusinessHours");
		scriptbhours.addParam("sysparm_startTime", newValue);



		scriptbhours.getXML(isstartDateWithinBusinessHours);
	}

	function isstartDateWithinBusinessHours(response){
			var scriptResponse = response.responseXML.documentElement.getAttribute("answer");
			if(scriptResponse != "true"){
			//g_form.addInfoMessage(scriptResponse)

					g_form.showErrorBox('start_datetime', 'Reservation must be within business hours: 9:00 - 21:00');
			}

	}


}
