(function executeRule(current, previous)  {
    var gr = $sp.getRecord();

    // Get table & sys_id
  //  data.table = input.table || $sp.getParameter("table");
    data.sys_id = $sp.getParameter("sys_id");


    // Valid GlideRecord
    gr = new GlideRecord('x_616811_car_renta_scheduled_reservations');


		if (!gr.isValid())
        return;

    // Valid sys_id

    if (!gr.get(data.sys_id))
        return;

if (input && input.action) {
        var action = input.action;
 if (action == 'cancel')
gr.deleteRecord();
}

})(current, previous);
