sp_widget.client_script:
function() {
 var c = this;
    c.uiAction = function(action) {

        c.data.action = action;
 if (c.data.action == 'delete') {
var ans = confirm("Are you sure, you want to delete it?");
      if (ans == true)
	update();
		else
		return false;
            }

 function update() {
            c.server.update().then(function() {
                c.data.action = undefined;
                c.data.result = "completed";
            });
            window.setTimeout(restartscreen, 2000);

            function restartscreen() {
                location.reload(true);
            }
        }
 };
}

sp_widget.script:
function() {
    var gr = $sp.getRecord();

    // Get table & sys_id
    data.table = input.table || $sp.getParameter("table");
    data.sys_id = input.sys_id || $sp.getParameter("sys_id");

    data.canRead = gr.canRead();
    if (!data.canRead)
        return;

    data.canWrite = gr.canWrite();
    if (!data.canWrite)
        return;

    var states = "";
    var b = $sp.getField(gr, 'state');
    if (b != null)
        states = b.value;

	var stages = "";
	var t = $sp.getField(gr, 'stage');
	if(t != null)
		stages = t.value;

	var ReqCancel = "";
	var c = $sp.getField(gr, 'u_asked_cancellation');
	if(c != null)
	ReqCancel = c.value;	


    // Valid GlideRecord
    gr = new GlideRecord(data.table);
    if (!gr.isValid())
        return;

    // Valid sys_id
    if (!gr.get(data.sys_id))
        return;

if (input && input.action) {
        var action = input.action;
 if (input.action == 'delete')
gr.deleteRecord();
}}
