sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here

	var months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

	var start = new GlideDateTime(current.getValue('start_datetime').toString());
	var end = new GlideDateTime(current.getValue('end_datetime').toString());

	var startDate = start.getDate();
	var endDate = end.getDate();

	var gr = new GlideRecord('x_616811_car_renta_calendar_days');
	gr.addQuery('date', '>=', startDate);
	gr.addQuery('date', '<=', endDate);
	gr.query();

	var holidays = [];

	while(gr.next()) {
		var tempArr = gr.date.toString().split('-');
		holidays.push(months[parseInt(tempArr[1]) - 1] + " " + tempArr[2]);
	}


	if (holidays.length != 0) {
		gs.addErrorMessage("Meetings cannot be reserved on Holidays (" + holidays.toString() + ")");
		current.setAbortAction(true);
	}


})(current, previous);
