sys_script_client.script:
function onLoad(){
	if(g_user.hasRole('x_616811_car_renta.admin,x_616811_car_renta.receptionist')){

		g_form.setReadOnly('applicant',false);
		g_form.setReadOnly('car',false);
		g_form.setReadOnly('car_image',false);
		g_form.setReadOnly('created',false);
		g_form.setReadOnly('created_by',false);
		g_form.setReadOnly('driver_s_license',false);
		g_form.setReadOnly('email',false);
		g_form.setReadOnly('end_datetime',false);
		g_form.setReadOnly('email_address',false);
		g_form.setReadOnly('guest_applicant',false);
		g_form.setReadOnly('driver',false);
		g_form.setReadOnly('number',false);
		g_form.setReadOnly('remarks',false);
		g_form.setReadOnly('rent_amount',false);
		g_form.setReadOnly('seat_capacity',false);
		g_form.setReadOnly('start_datetime',false);
		g_form.setReadOnly('sys_updated_by',false);
		g_form.setReadOnly('sys_mod_count',false);
		g_form.setReadOnly('sys_updated_on',false);

	}

	else if(g_form.getValue('applicant') != g_user.userID){
		// make all fields readonly
		g_form.setReadOnly('applicant',true);
		g_form.setReadOnly('car',true);
		g_form.setReadOnly('car_image',true);
		g_form.setReadOnly('created',true);
		g_form.setReadOnly('created_by',true);
		g_form.setReadOnly('driver_s_license',true);
		g_form.setReadOnly('email',true);
		g_form.setReadOnly('end_datetime',true);
		g_form.setReadOnly('email_address',true);
		g_form.setReadOnly('guest_applicant',true);
		g_form.setReadOnly('driver',true);
		g_form.setReadOnly('number',true);
		g_form.setReadOnly('remarks',true);
		g_form.setReadOnly('rent_amount',true);
		g_form.setReadOnly('seat_capacity',true);
		g_form.setReadOnly('start_datetime',true);
		g_form.setReadOnly('sys_updated_by',true);
		g_form.setReadOnly('sys_mod_count',true);
		g_form.setReadOnly('sys_updated_on',true);

	}
	else if(g_form.getValue('applicant') == g_user.userID){
		// make all fields readonly

		g_form.setReadOnly('applicant',false);
		g_form.setReadOnly('car',false);
		g_form.setReadOnly('car_image',false);
		g_form.setReadOnly('created',false);
		g_form.setReadOnly('created_by',false);
		g_form.setReadOnly('driver_s_license',false);
		g_form.setReadOnly('email',false);
		g_form.setReadOnly('end_datetime',false);
		g_form.setReadOnly('email_address',false);
		g_form.setReadOnly('guest_applicant',false);
		g_form.setReadOnly('driver',false);
		g_form.setReadOnly('number',false);
		g_form.setReadOnly('remarks',false);
		g_form.setReadOnly('rent_amount',false);
		g_form.setReadOnly('seat_capacity',false);
		g_form.setReadOnly('start_datetime',false);
		g_form.setReadOnly('sys_updated_by',false);
		g_form.setReadOnly('sys_mod_count',false);
		g_form.setReadOnly('sys_updated_on',false);

	}
	else if(g_form.getValue('applicant') == '' && g_user.userID == "Allan Schwantd"){
		g_form.setReadOnly('applicant',false);
		g_form.setReadOnly('car',false);
		g_form.setReadOnly('car_image',false);
		g_form.setReadOnly('created',false);
		g_form.setReadOnly('created_by',false);
		g_form.setReadOnly('driver_s_license',false);
		g_form.setReadOnly('email',false);
		g_form.setReadOnly('end_datetime',false);
		g_form.setReadOnly('email_address',false);
		g_form.setReadOnly('guest_applicant',false);
		g_form.setReadOnly('driver',false);
		g_form.setReadOnly('number',false);
		g_form.setReadOnly('remarks',false);
		g_form.setReadOnly('rent_amount',false);
		g_form.setReadOnly('seat_capacity',false);
		g_form.setReadOnly('start_datetime',false);
		g_form.setReadOnly('sys_updated_by',false);
		g_form.setReadOnly('sys_mod_count',false);
		g_form.setReadOnly('sys_updated_on',false);
	}

}
