sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
    return;
   }
   //Type appropriate comment here, and begin script below

	//var image = g_form.getReference('car', doAlert); // doAlert is our callback function
	//var carimage = new GlideRecord('sys_attachment', image);

	var carimage = new GlideAjax('GetImagePicture');
	carimage.addParam('sysparm_name','getImage');
	carimage.addParam('sysparm_sys_id', g_form.getValue('car'));
	carimage.getXML(populateImage);


}
function populateImage(response) { //reference is passed into callback as first arguments

	var imagefromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('car_image', imagefromScriptInclude);




}
