sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	var holiday = new GlideAjax("CarRentalUtils");
	holiday.addParam("sysparm_name", "isDateOnHoliday");
	holiday.addParam("sysparm_dateTime", newValue);


	holiday.getXML(isDateOnHoliday);

	function isDateOnHoliday(response){

			var scriptResponse = response.responseXML.documentElement.getAttribute("answer");
			if(scriptResponse == "true"){
				g_form.showErrorBox("end_datetime","There are no reservations during holidays. Please pick another date.", true);
			}
	}

	if(g_form.getValue("end_datetime") != "" ){
		var today = new Date();

		var enddate = new Date(g_form.getValue('end_datetime'));

		var enddatetime = enddate.getHours();

	}

	if ( enddatetime < 9 ||  enddatetime > 21) {
		g_form.showErrorBox('end_datetime', 'Reservation must be within business hours: 9:00 - 21:00');
	}

	var enddatetimeweek = enddate.getDay();

    // Check if saturday or sunday
	if (enddatetimeweek  == 6 || enddatetimeweek  == 0) {
		g_form.showErrorBox('end_datetime', 'Reservation must be within business days: Mon-Fri');
		return false;
	}

}
