sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
	var startoverlap = new GlideAjax("CarRentalUtils");
	startoverlap.addParam("sysparm_name", "isstartDateOverlappingstart");
	startoverlap.addParam("sysparm_dateTime", newValue);
	startoverlap.addParam("sysparm_endTime", g_form.getValue('end_datetime'));
	startoverlap.addParam("sysparm_car", g_form.getValue('car'));

	startoverlap.getXML(isstartDateOverlappingstart);



	function isstartDateOverlappingstart(response){
			var answer = response.responseXML.documentElement.getAttribute("answer");
			if(answer == "true"){
				g_form.showErrorBox("start_datetime","This car is already reserved on the selected date. Please pick another date.", true);
			}
	}

   //Type appropriate comment here, and begin script below

}
