sys_script_client.script:
function onSubmit() {

   //Type appropriate comment here, and begin script below

	var rentprice1 = new GlideAjax("CarRentalUtils");
	rentprice1.addParam("sysparm_name", "rentPriceNotify1");
	rentprice1.addParam("sysparm_rentprice", g_form.getDecimalValue('rent_amount'));
	rentprice1.addParam("sysparm_startdatetime", g_form.getValue('start_datetime'));

	rentprice1.addParam("sysparm_enddatetime", g_form.getValue('end_datetime'));

	rentprice1.getXML(rentPriceNotify1);

	function rentPriceNotify1(response){
			var answer = response.responseXML.documentElement.getAttribute("answer");

			g_form.addInfoMessage(answer);

	}


}
