sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }
	var startoverlap = new GlideAjax("CarRentalUtils");
	startoverlap.addParam("sysparm_name", "isstartDateOverlappingend");
	startoverlap.addParam("sysparm_dateTime", newValue);

	startoverlap.getXML(isstartDateOverlappingend);

	function isstartDateOverlappingend(response){
			var answer = response.responseXML.documentElement.getAttribute("answer");
			if(answer == "true"){
				g_form.showErrorBox("start_datetime","Selected date is already occupied change Start datetime to another date", true);
			}
	}

   //Type appropriate comment here, and begin script below

}
