sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
//isDateWithinBusinessHours

	if(g_form.getValue("start_datetime") != "" ){

		var today = new Date();

		var start = new Date(g_form.getValue('start_datetime'));


		var startdatetime = start.getHours();



	}


	if (startdatetime < 9 ||  startdatetime > 21) {
		g_form.showErrorBox('start_datetime', 'Start datetime must be within business hours: 9:00 - 21:00');
	}



	var startdatetimeweek = start.getDay();


    // Check if saturday or sunday
	if (startdatetimeweek == 6 || startdatetimeweek == 0) {
		g_form.showErrorBox('start_datetime', 'Start datetime must be within Business Days: Mon-Fri');
		return false;
	}

}
