	rentPriceNotify: function(){
	var start = new GlideDateTime(this.getParameter('sysparm_startdatetime'));
	var end = new GlideDateTime(this.getParameter('sysparm_enddatetime'));
	var price = this.getParameter('sysparm_rentprice');
	var diff = GlideDateTime.subtract(start, end);
	var days = diff.getRoundedDayPart();



	return 'Prepare the said amount ₱'+ days * price +' on or before rent duration' ;
	},

		rentPriceNotify1: function(){
	var start = new GlideDateTime(this.getParameter('sysparm_startdatetime'));
	var end = new GlideDateTime(this.getParameter('sysparm_enddatetime'));
	var price = this.getParameter('sysparm_rentprice');


	var diff = GlideDateTime.subtract(start, end);
	var days = diff.getRoundedDayPart();



	return 'Rent Price updated Prepare the said amount ₱'+ days * price +' on or before rent duration' ;
	},

